INSERT INTO `comments` VALUES (1,'Bài viết rất hay!',1,1,NULL,'2025-03-31 04:06:54','2025-03-31 04:06:54'),(2,'Tôi thấy thông tin này rất hữu ích.',4,1,NULL,'2025-03-31 04:06:54','2025-03-31 04:06:54'),(3,'Bạn có thể giải thích thêm về chủ đề này không?',7,2,NULL,'2025-03-31 04:06:54','2025-03-31 04:06:54'),(5,'Bài viết được viết rất tốt!',1,4,NULL,'2025-03-31 04:06:54','2025-03-31 04:06:54'),(6,'Đây chính là những gì tôi đang tìm kiếm.',4,5,NULL,'2025-03-31 04:06:54','2025-03-31 04:06:54'),(7,'Quan điểm thú vị.',7,6,1,'2025-03-31 04:06:54','2025-03-31 14:54:32'),(9,'Thông tin này giúp ích rất nhiều cho tôi.',1,8,1,'2025-03-31 04:06:54','2025-03-31 14:54:29'),(10,'Cảm ơn vì đã chia sẻ!',4,9,9,'2025-03-31 04:06:54','2025-03-31 14:54:42'),(11,'vàng tăng,usd tăng,xăng tăng mà ... lương thì cứ đứng yên.',4,21,NULL,'2025-04-03 15:25:18','2025-04-03 15:25:18'),(12,'Hay quá. Vào bắt đáy để ăn quả hồi rồi thoát hàng ngay nhé vì sau quả hồi sẽ còn giảm nữa.',4,16,NULL,'2025-04-03 15:27:22','2025-04-03 15:27:22'),(14,'Tỷ giá sẽ neo cao tiệm cận 30k quá, hichic',11,19,NULL,'2025-04-03 15:31:24','2025-04-03 15:32:38'),(15,'Không gây thương vong đã là may mắn rồi!',1,22,NULL,'2025-04-03 15:29:52','2025-04-03 15:32:50'),(16,'Đề nghị xử phạt nghiêm minh đúng người đúng tội',4,17,NULL,'2025-04-03 15:33:45','2025-04-03 15:33:45'),(17,'Tôi hoàn toàn thống nhất phương án giảm 50% cấp xã phường đây là phương án tối ưu nhất ',1,15,NULL,'2025-04-03 15:35:05','2025-04-03 15:35:05'),(18,'Nếu thiết kế, thi công đạt chuẩn, tại sao hơn 300 căn hộ lại xuất hiện vết nứt? Cùng một rung chấn, sao những chung cư khác không bị ảnh hưởng tương tự?\r\nĐiều người dân cần là kiểm tra minh bạch, có trách nhiệm, và quan trọng nhất một cam kết rõ ràng về quyền lợi nếu sự cố xảy ra !!',4,14,NULL,'2025-04-03 15:35:52','2025-04-03 15:35:52'),(19,'Chỉ dưới thời ông Trou thì Việt Nam mới thua Hàn 0-6 do triết lý ảo tưởng cầm bóng tấn công, còn Việt Nam đối đầu với Hàn Quốc hay Nhật, nếu biết mình biết ta thì chẳng báo giờ thua đậm và hoàn toàn có thể thủ hòa thậm chí thắng như thời ông Alfred Riedl từng thắng Hàn 1-0 ở vòng loại WC với bàn thắng của Văn Quyến.',7,13,NULL,'2025-04-03 15:36:33','2025-04-03 15:36:33'),(20,'Thích nhất ông \"dàn cảnh\" thua giao hữu ở Mỹ Đình rình rập thắng đẳng cấp ở hai lượt trận sân nhà sân khách, đặc biệt lãnh cúp ở Băng Cốc. \"Phục hồi\" danh dự trước Indo với chiến thuật xoay tua đội hình giữ thể lực bền bỉ và thành công vang dội.',1,13,NULL,'2025-04-03 15:37:01','2025-04-03 15:37:01'),(21,'Vĩnh biệt ông, người đồng chí của Việt Nam',1,12,NULL,'2025-04-03 15:37:57','2025-04-03 15:37:57'),(22,'Kính cẩn nghiêng mình tiếc thương Ông.',7,12,NULL,'2025-04-03 15:38:16','2025-04-03 15:38:16'),(23,'Chúng ta đã chuẩn bị cho tất cả các tình huống , không bị động, bất ngờ.... Yên tâm nhé',4,11,NULL,'2025-04-03 15:39:42','2025-04-03 15:39:42'),(24,'đúng vậy',11,11,23,'2025-04-03 15:40:50','2025-04-03 15:40:50'),(25,'Quá buồn',11,12,21,'2025-04-03 16:32:33','2025-04-03 16:32:33'),(26,'Buồn vl luôn',11,12,25,'2025-04-03 16:32:43','2025-04-03 16:32:43'),(27,'Buồn vcl\n',11,12,25,'2025-04-03 16:32:56','2025-04-03 16:32:56'),(28,'Haizz, khó nói',11,12,22,'2025-04-03 16:33:10','2025-04-03 16:33:10'),(29,'Không sao cả',11,12,NULL,'2025-04-03 16:33:33','2025-04-03 16:33:33'),(30,'Không vấn đề gì',11,12,NULL,'2025-04-03 16:33:41','2025-04-03 16:33:41'),(31,'Không sao cc',11,12,29,'2025-04-03 16:33:46','2025-04-03 16:33:46'),(32,'Bình luận như cái cc',11,12,30,'2025-04-03 16:34:05','2025-04-03 16:34:05'),(33,'câp3 ',11,12,32,'2025-04-03 16:34:15','2025-04-03 16:34:15'),(34,'123',11,12,32,'2025-04-03 18:48:58','2025-04-03 18:48:58'),(35,'123',11,12,21,'2025-04-03 18:49:04','2025-04-03 18:49:04'),(36,'123123',11,12,35,'2025-04-03 18:49:09','2025-04-03 18:49:09'),(38,'123456',11,12,21,'2025-04-03 18:58:46','2025-04-03 18:58:46'),(39,'123456',11,12,32,'2025-04-03 19:01:33','2025-04-03 19:01:33'),(40,'CCCCCCCCCCCCCCCCC',11,2,NULL,'2025-04-04 02:56:14','2025-04-04